// src/pages/ExpensesPage.tsx
import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';
import apiClient from '../api/axios';
import { Expense, CreateExpenseRequest } from '../types';
import '../styles/dashboard.css';
import '../styles/expenses.css';

const ExpensesPage = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  
  // NOWY STAN: Przechowuje ID edytowanego elementu (null = tryb dodawania)
  const [editingId, setEditingId] = useState<number | null>(null);

  const { register, handleSubmit, reset, setValue, formState: { errors } } = useForm<CreateExpenseRequest>({
    defaultValues: {
      date: new Date().toISOString().split('T')[0]
    }
  });

  // 1. Pobieranie listy
  const fetchExpenses = async () => {
    try {
      const response = await apiClient.get<Expense[]>('/api/Expenses');
      const sorted = response.data.sort((a, b) => 
        new Date(b.date).getTime() - new Date(a.date).getTime()
      );
      setExpenses(sorted);
    } catch (error) {
      console.error("B≈ÇƒÖd pobierania:", error);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  // 2. Obs≈Çuga zapisu (DODAWANIE lub EDYCJA)
  const onSubmit = async (data: CreateExpenseRequest) => {
    setIsLoading(true);
    try {
      const payload = {
        ...data,
        date: new Date(data.date).toISOString(),
        amount: Number(data.amount)
      };

      if (editingId) {
        // --- TRYB EDYCJI (PUT) ---
        await apiClient.put(`/api/Expenses/${editingId}`, payload);
        alert("Zapisano zmiany!");
      } else {
        // --- TRYB DODAWANIA (POST) ---
        await apiClient.post('/api/Expenses', payload);
      }
      
      resetForm(); // Czy≈õcimy formularz
      await fetchExpenses(); // Od≈õwie≈ºamy listƒô
    } catch (error) {
      console.error("B≈ÇƒÖd zapisu:", error);
      alert("WystƒÖpi≈Ç b≈ÇƒÖd podczas zapisu.");
    } finally {
      setIsLoading(false);
    }
  };

  // 3. Rozpoczƒôcie edycji (wrzucenie danych do formularza)
  const handleEdit = (expense: Expense) => {
    setEditingId(expense.id);
    
    // Wype≈Çniamy pola formularza danymi z wiersza
    setValue("categoryName", expense.categoryName);
    setValue("amount", expense.amount);
    setValue("note", expense.note || "");
    // Formatujemy datƒô do YYYY-MM-DD dla input type="date"
    setValue("date", new Date(expense.date).toISOString().split('T')[0]);
    
    // Przewijamy do g√≥ry, ≈ºeby u≈ºytkownik zobaczy≈Ç formularz
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // 4. Usuwanie
  const handleDelete = async (id: number) => {
    if (!window.confirm("Czy na pewno chcesz usunƒÖƒá ten wydatek?")) return;

    try {
      await apiClient.delete(`/api/Expenses/${id}`);
      // Usuwamy z listy lokalnie (szybciej ni≈º ponowne pobieranie)
      setExpenses(prev => prev.filter(e => e.id !== id));
    } catch (error) {
      console.error("B≈ÇƒÖd usuwania:", error);
      alert("Nie uda≈Ço siƒô usunƒÖƒá wpisu.");
    }
  };

  // 5. Anulowanie edycji
  const resetForm = () => {
    setEditingId(null);
    reset({
      categoryName: "Inne", // Domy≈õlna kategoria
      amount: undefined,
      note: "",
      date: new Date().toISOString().split('T')[0]
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2>MyBudget</h2>
        <nav>
          <Link to="/">Pulpit</Link>
          <Link to="/expenses" style={{ color: 'white', fontWeight: 'bold' }}>Wydatki</Link>
          <Link to="/income">Przychody</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>Wyloguj siƒô</button>
        </div>
      </aside>

      <main className="main-content">
        <h1>Twoje Wydatki</h1>

        {/* --- FORMULARZ (ZMIENIA SIƒò W ZALE≈ªNO≈öCI OD TRYBU) --- */}
        <div className="expense-form-card" style={editingId ? {border: '2px solid #ffc107'} : {}}>
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 15}}>
            <h3>{editingId ? 'Edytuj wydatek' : 'Dodaj nowy wydatek'}</h3>
            {editingId && (
              <button onClick={resetForm} className="cancel-btn">Anuluj edycjƒô</button>
            )}
          </div>
          
          <form onSubmit={handleSubmit(onSubmit)} className="expense-form">
            <div className="form-group-inline">
              <label>Kategoria</label>
              <select {...register("categoryName", { required: true })}>
                <option value="Jedzenie">Jedzenie</option>
                <option value="Mieszkanie">Mieszkanie</option>
                <option value="Transport">Transport</option>
                <option value="Rozrywka">Rozrywka</option>
                <option value="Inne">Inne</option>
              </select>
            </div>

            <div className="form-group-inline">
              <label>Kwota (PLN)</label>
              <input type="number" step="0.01" {...register("amount", { required: true })} />
            </div>

            <div className="form-group-inline">
              <label>Data</label>
              <input type="date" {...register("date", { required: true })} />
            </div>

            <div className="form-group-inline" style={{flexGrow: 2}}>
              <label>Notatka</label>
              <input type="text" {...register("note")} />
            </div>

            <button type="submit" disabled={isLoading} className={editingId ? "save-btn" : "add-btn"}>
              {isLoading ? '...' : (editingId ? 'Zapisz' : 'Dodaj')}
            </button>
          </form>
        </div>

        {/* --- TABELA --- */}
        <div className="table-card">
          <table className="custom-table">
            <thead>
              <tr>
                <th>Data</th>
                <th>Kategoria</th>
                <th>Opis</th>
                <th style={{textAlign: 'right'}}>Kwota</th>
                <th style={{textAlign: 'center'}}>Akcje</th> {/* Nowa kolumna */}
              </tr>
            </thead>
            <tbody>
              {expenses.map((exp) => (
                <tr key={exp.id} style={editingId === exp.id ? {backgroundColor: '#fff3cd'} : {}}>
                  <td>{new Date(exp.date).toLocaleDateString()}</td>
                  <td><span className="badge">{exp.categoryName || 'Inne'}</span></td>
                  <td>{exp.note}</td>
                  <td style={{textAlign: 'right', fontWeight: 'bold', color: '#dc3545'}}>
                    -{exp.amount.toFixed(2)} PLN
                  </td>
                  
                  {/* PRZYCISKI AKCJI */}
                  <td style={{textAlign: 'center'}}>
                    <button 
                      onClick={() => handleEdit(exp)} 
                      className="action-btn edit-btn"
                      title="Edytuj"
                    >
                      ‚úèÔ∏è
                    </button>
                    <button 
                      onClick={() => handleDelete(exp.id)} 
                      className="action-btn delete-btn"
                      title="Usu≈Ñ"
                    >
                      üóëÔ∏è
                    </button>
                  </td>
                </tr>
              ))}
              {expenses.length === 0 && (
                <tr><td colSpan={5} style={{textAlign: 'center', padding: 20}}>Brak danych</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

export default ExpensesPage;
