// src/pages/ExpensesPage.tsx
import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';
import apiClient from '../api/axios';
import { Expense, CreateExpenseRequest } from '../types';
import '../styles/dashboard.css'; // Używamy układu dashboardu
import '../styles/expenses.css';  // Dodatkowe style dla tabeli (stworzymy je za chwilę)

const ExpensesPage = () => {
  // 1. Stan aplikacji
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  
  // 2. Obsługa formularza
  const { register, handleSubmit, reset, formState: { errors } } = useForm<CreateExpenseRequest>({
    defaultValues: {
      date: new Date().toISOString().split('T')[0] // Domyślnie dzisiejsza data
    }
  });

  // 3. Pobieranie listy wydatków
  const fetchExpenses = async () => {
    try {
      const response = await apiClient.get<Expense[]>('/api/Expenses');
      // Sortujemy: najnowsze na górze
      const sorted = response.data.sort((a, b) => 
        new Date(b.date).getTime() - new Date(a.date).getTime()
      );
      setExpenses(sorted);
    } catch (error) {
      console.error("Błąd pobierania wydatków:", error);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  // 4. Wysyłanie nowego wydatku
  const onSubmit = async (data: CreateExpenseRequest) => {
    setIsLoading(true);
    try {
      // Backend oczekuje pełnej daty ISO, formularz daje YYYY-MM-DD
      const payload = {
        ...data,
        date: new Date(data.date).toISOString(), // Konwersja daty
        amount: Number(data.amount) // Upewniamy się, że to liczba
      };

      await apiClient.post('/api/Expenses', payload);
      
      // Sukces! Czyścimy formularz i odświeżamy listę
      reset(); 
      await fetchExpenses();
    } catch (error) {
      console.error("Błąd dodawania:", error);
      alert("Nie udało się dodać wydatku.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  return (
    <div className="dashboard-container">
      {/* SIDEBAR (taki sam jak w Dashboard) */}
      <aside className="sidebar">
        <h2>MyBudget</h2>
        <nav>
          <Link to="/">Pulpit</Link>
          <Link to="/expenses" style={{ color: 'white', fontWeight: 'bold' }}>Wydatki</Link>
          <Link to="/income">Przychody</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>Wyloguj się</button>
        </div>
      </aside>

      {/* GŁÓWNA ZAWARTOŚĆ */}
      <main className="main-content">
        <h1>Twoje Wydatki</h1>

        {/* --- FORMULARZ DODAWANIA --- */}
        <div className="expense-form-card">
          <h3>Dodaj nowy wydatek</h3>
          <form onSubmit={handleSubmit(onSubmit)} className="expense-form">
            
            <div className="form-group-inline">
              <label>Kategoria</label>
              {/* Na razie prosta lista, później pobierzemy z API */}
              <select {...register("categoryName", { required: true })}>
                <option value="Jedzenie">Jedzenie</option>
                <option value="Mieszkanie">Mieszkanie</option>
                <option value="Transport">Transport</option>
                <option value="Rozrywka">Rozrywka</option>
                <option value="Inne">Inne</option>
              </select>
            </div>

            <div className="form-group-inline">
              <label>Kwota (PLN)</label>
              <input 
                type="number" 
                step="0.01" 
                {...register("amount", { required: true, min: 0.01 })} 
              />
            </div>

            <div className="form-group-inline">
              <label>Data</label>
              <input 
                type="date" 
                {...register("date", { required: true })} 
              />
            </div>

            <div className="form-group-inline" style={{flexGrow: 2}}>
              <label>Notatka (opcjonalnie)</label>
              <input 
                type="text" 
                placeholder="np. Pizza z kolegami"
                {...register("note")} 
              />
            </div>

            <button type="submit" disabled={isLoading} className="add-btn">
              {isLoading ? '+' : 'Dodaj'}
            </button>
          </form>
        </div>

        {/* --- TABELA Z LISTĄ --- */}
        <div className="table-card">
          <table className="custom-table">
            <thead>
              <tr>
                <th>Data</th>
                <th>Kategoria</th>
                <th>Opis</th>
                <th style={{textAlign: 'right'}}>Kwota</th>
              </tr>
            </thead>
            <tbody>
              {expenses.length > 0 ? (
                expenses.map((exp) => (
                  <tr key={exp.id}>
                    <td>{new Date(exp.date).toLocaleDateString()}</td>
                    <td>
                      <span className="badge">{exp.categoryName || 'Inne'}</span>
                    </td>
                    <td>{exp.note}</td>
                    <td style={{textAlign: 'right', fontWeight: 'bold', color: '#dc3545'}}>
                      -{exp.amount.toFixed(2)} PLN
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={4} style={{textAlign: 'center', padding: 20}}>
                    Brak wydatków. Dodaj pierwszy powyżej!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

export default ExpensesPage;
