// src/pages/ExpensesPage.tsx
import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';
import apiClient from '../api/axios';
import { Expense, CreateExpenseRequest, Category } from '../types'; // Upewnij siƒô, ≈ºe importujesz Category
import '../styles/dashboard.css';
import '../styles/expenses.css';

const ExpensesPage = () => {
  // Stany
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [categories, setCategories] = useState<Category[]>([]); // <--- NOWY STAN DLA KATEGORII
  const [isLoading, setIsLoading] = useState(false);
const [editingId, setEditingId] = useState<string | null>(null);

  const { register, handleSubmit, reset, setValue, formState: { errors } } = useForm<CreateExpenseRequest>({
    defaultValues: {
      date: new Date().toISOString().split('T')[0]
    }
  });

  // 1. Pobieranie danych (Wydatki + Kategorie)
  const fetchData = async () => {
    try {
      // Pobieramy r√≥wnolegle wydatki i kategorie
      const [expensesRes, categoriesRes] = await Promise.all([
        apiClient.get<Expense[]>('/api/Expenses'),
        apiClient.get<Category[]>('/api/Category')
      ]);

      // Sortujemy wydatki (najnowsze na g√≥rze)
      const sortedExpenses = expensesRes.data.sort((a, b) => 
        new Date(b.date).getTime() - new Date(a.date).getTime()
      );
      setExpenses(sortedExpenses);

      // Sortujemy kategorie alfabetycznie
      setCategories(categoriesRes.data.sort((a, b) => a.name.localeCompare(b.name)));

    } catch (error) {
      console.error("B≈ÇƒÖd pobierania danych:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // 2. Obs≈Çuga formularza (Dodawanie/Edycja)
  const onSubmit = async (data: CreateExpenseRequest) => {
    setIsLoading(true);
    try {
      // 1. Znajdujemy obiekt kategorii na podstawie nazwy z formularza
      const selectedCategory = categories.find(c => c.name === data.categoryName);
      
      // Je≈õli nie znaleziono kategorii (np. "Inne" lub b≈ÇƒÖd), musimy zdecydowaƒá co wys≈Çaƒá.
      // Zak≈Çadam, ≈ºe backend przyjmuje null lub musisz mieƒá kategoriƒô "Inne" w bazie.
      const categoryIdToSend = selectedCategory ? selectedCategory.id : null;

      const payload = {
        id: editingId, // Przy edycji warto wys≈Çaƒá ID w ciele (backendy tego czƒôsto wymagajƒÖ)
        amount: Number(data.amount),
        date: new Date(data.date).toISOString(), // Format daty dla C#
        note: data.note,
        categoryId: categoryIdToSend // <--- KLUCZOWE: Wysy≈Çamy ID, nie nazwƒô!
      };

      if (editingId) {
        // EDYCJA
        await apiClient.put(`/api/Expenses/${editingId}`, payload);
      } else {
        // DODAWANIE
        await apiClient.post('/api/Expenses', payload);
      }
      
      resetForm();
      
      // Od≈õwie≈º listƒô
      const res = await apiClient.get<Expense[]>('/api/Expenses');
      const sorted = res.data.sort((a,b) => new Date(b.date).getTime() - new Date(a.date).getTime());
      setExpenses(sorted);

    } catch (error) {
      console.error("B≈ÇƒÖd zapisu:", error);
      alert("WystƒÖpi≈Ç b≈ÇƒÖd podczas zapisu. Sprawd≈∫ czy wybra≈Çe≈õ kategoriƒô.");
    } finally {
      setIsLoading(false);
    }
  };

  

  const handleDelete = async (id: string) => {
    if (!window.confirm("UsunƒÖƒá ten wydatek?")) return;
    try {
      await apiClient.delete(`/api/Expenses/${id}`);
      setExpenses(prev => prev.filter(e => e.id !== id));
    } catch (error) {
      console.error("B≈ÇƒÖd usuwania:", error);
    }
  };

 const handleEdit = (expense: Expense) => {
    setEditingId(expense.id);
    const matchedCategory = categories.find(c => c.id === expense.categoryId);
    setValue("categoryName", matchedCategory ? matchedCategory.name : "Inne");
    setValue("amount", expense.amount);
    setValue("note", expense.note || "");
    setValue("date", new Date(expense.date).toISOString().split('T')[0]);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const resetForm = () => {
    setEditingId(null);
    reset({
      amount: undefined,
      note: "",
      date: new Date().toISOString().split('T')[0]
      // Nie resetujemy kategorii na "Inne", niech zostanie ta co by≈Ça lub pierwsza z listy
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2>MyBudget</h2>
        <nav>
          <Link to="/">Pulpit</Link>
          <Link to="/expenses" style={{ color: 'white', fontWeight: 'bold' }}>Wydatki</Link>
          <Link to="/income">Przychody</Link>
          <Link to="/settings">Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>Wyloguj siƒô</button>
        </div>
      </aside>

      <main className="main-content">
        <h1>Twoje Wydatki</h1>

        {/* --- FORMULARZ --- */}
        <div className="expense-form-card" style={editingId ? {border: '2px solid #ffc107'} : {}}>
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 15}}>
             <h3>{editingId ? 'Edytuj wydatek' : 'Dodaj nowy wydatek'}</h3>
             {editingId && <button onClick={resetForm} className="cancel-btn">Anuluj</button>}
          </div>

          <form onSubmit={handleSubmit(onSubmit)} className="expense-form">
            
            <div className="form-group-inline">
              <label>Kategoria</label>
              
              {/* TU JEST KLUCZOWA ZMIANA - LISTA DYNAMICZNA */}
              <select {...register("categoryName", { required: true })}>
                <option value="" disabled>Wybierz...</option>
                {categories.length > 0 ? (
                  categories.map(cat => (
                    <option key={cat.id} value={cat.name}>
                      {cat.name}
                    </option>
                  ))
                ) : (
                  <option value="Inne">Brak kategorii (za≈Çaduj w Ustawieniach)</option>
                )}
              </select>

            </div>

            <div className="form-group-inline">
              <label>Kwota</label>
              <input type="number" step="0.01" {...register("amount", { required: true })} />
            </div>

            <div className="form-group-inline">
              <label>Data</label>
              <input type="date" {...register("date", { required: true })} />
            </div>

            <div className="form-group-inline" style={{flexGrow: 2}}>
              <label>Notatka</label>
              <input type="text" {...register("note")} />
            </div>

            <button type="submit" disabled={isLoading} className={editingId ? "save-btn" : "add-btn"}>
              {editingId ? 'Zapisz' : 'Dodaj'}
            </button>
          </form>
        </div>

        {/* --- TABELA --- */}
        <div className="table-card">
          <table className="custom-table">
            <thead>
              <tr>
                <th>Data</th>
                <th>Kategoria</th>
                <th>Opis</th>
                <th style={{textAlign: 'right'}}>Kwota</th>
                <th style={{textAlign: 'center'}}>Akcje</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((exp) => {
                // TUTAJ JEST NAPRAWA:
                // Szukamy kategorii, kt√≥rej ID pasuje do categoryId wydatku
                const matchedCategory = categories.find(c => c.id === exp.categoryId);
                
                return (
                  <tr key={exp.id} style={editingId === exp.id ? {backgroundColor: '#fff3cd'} : {}}>
                    <td>{new Date(exp.date).toLocaleDateString()}</td>
                    <td>
                      <span className="badge">
                        {/* Wy≈õwietlamy nazwƒô znalezionej kategorii lub 'Inne' */}
                        {matchedCategory ? matchedCategory.name : 'Inne'}
                      </span>
                    </td>
                    <td>{exp.note}</td>
                    <td style={{textAlign: 'right', fontWeight: 'bold', color: '#dc3545'}}>
                      -{exp.amount.toFixed(2)} PLN
                    </td>
                    <td style={{textAlign: 'center'}}>
                      <button onClick={() => handleEdit(exp)} className="action-btn edit-btn">‚úèÔ∏è</button>
                      <button onClick={() => handleDelete(exp.id)} className="action-btn delete-btn">üóëÔ∏è</button>
                    </td>
                  </tr>
                );
              })}
              {expenses.length === 0 && <tr><td colSpan={5} style={{textAlign:'center', padding:20}}>Brak wydatk√≥w</td></tr>}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

export default ExpensesPage;
