// src/pages/Dashboard.tsx
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import apiClient from '../api/axios';
import { Expense, Income, Category } from '../types';
import '../styles/dashboard.css';

const COLORS = ['#3498db', '#2ecc71', '#9b59b6', '#f1c40f', '#e67e22', '#e74c3c', '#95a5a6'];

const Dashboard = () => {
  const navigate = useNavigate();
  
  // Pomocnicza funkcja: Pierwszy dzie≈Ñ obecnego miesiƒÖca
  const getFirstDayOfMonth = () => {
    const date = new Date();
    return new Date(date.getFullYear(), date.getMonth(), 1).toISOString().split('T')[0];
  };

  // Pomocnicza funkcja: Dzisiaj
  const getToday = () => {
    return new Date().toISOString().split('T')[0];
  };

  // --- STANY ---
  const [balance, setBalance] = useState<number>(0);
  
  // 1. Wszystkie dane z API (surowe)
  const [allExpenses, setAllExpenses] = useState<Expense[]>([]);
  
  // 2. Dane przefiltrowane (do wy≈õwietlania)
  const [displayedExpenses, setDisplayedExpenses] = useState<Expense[]>([]);
  const [chartData, setChartData] = useState<{ name: string; value: number }[]>([]);

  const [categories, setCategories] = useState<Category[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  // 3. Filtry daty (Domy≈õlnie: od 1. dnia miesiƒÖca do dzisiaj)
  const [startDate, setStartDate] = useState(getFirstDayOfMonth());
  const [endDate, setEndDate] = useState(getToday());

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  // EFEKT 1: Pobranie danych z API (tylko raz przy wej≈õciu)
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [expensesRes, incomeRes, categoriesRes] = await Promise.all([
          apiClient.get<Expense[]>('/api/Expenses'),
          apiClient.get<Income[]>('/api/Income'),
          apiClient.get<Category[]>('/api/Category')
        ]);

        // Zapisujemy surowe dane
        setAllExpenses(expensesRes.data); 
        setCategories(categoriesRes.data);

        // Obliczamy GLOBALNE saldo (wszystkie przychody - wszystkie wydatki)
        // Saldo zazwyczaj pokazuje stan konta og√≥≈Çem, wiƒôc nie filtrujemy go datƒÖ (chyba ≈ºe chcesz inaczej)
        const totalIncome = incomeRes.data.reduce((sum, item) => sum + item.amount, 0);
        const totalExpenses = expensesRes.data.reduce((sum, item) => sum + item.amount, 0);
        setBalance(totalIncome - totalExpenses);

      } catch (error) {
        console.error("B≈ÇƒÖd danych:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  // EFEKT 2: Filtrowanie danych (uruchamia siƒô, gdy zmienisz datƒô lub pobierzesz dane)
  useEffect(() => {
    if (allExpenses.length === 0) return;

    // 1. Filtrujemy listƒô po dacie
    const filtered = allExpenses.filter(exp => {
        // Obcinamy czas z daty wydatku (YYYY-MM-DD), ≈ºeby por√≥wnanie dzia≈Ça≈Ço dobrze
        const expDate = exp.date.split('T')[0];
        return expDate >= startDate && expDate <= endDate;
    });

    // Sortowanie: najnowsze na g√≥rze
    filtered.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

    // Aktualizujemy listƒô do wy≈õwietlenia
    setDisplayedExpenses(filtered);

    // 2. Przeliczamy dane do wykresu na podstawie PRZEFILTROWANEJ listy
    const categoryMap = new Map<string, number>();

    filtered.forEach(exp => {
      const matchedCat = categories.find(c => c.id === exp.categoryId);
      const catName = matchedCat ? matchedCat.name : "Inne";
      const currentTotal = categoryMap.get(catName) || 0;
      categoryMap.set(catName, currentTotal + exp.amount);
    });

    const chartDataArray = Array.from(categoryMap, ([name, value]) => ({ name, value }));
    setChartData(chartDataArray);

  }, [startDate, endDate, allExpenses, categories]);


  if (isLoading) {
    return <div className="dashboard-container" style={{display:'flex',justifyContent:'center',alignItems:'center'}}><h2>≈Åadowanie...</h2></div>;
  }

  return (
    <div className="dashboard-container">
      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2>üí∞ MyBudget</h2>
        <nav>
          <Link to="/">üìä Pulpit</Link>
          <Link to="/expenses">üí∏ Wydatki</Link>
          <Link to="/income">üí∞ Przychody</Link>
          <Link to="/settings">‚öôÔ∏è Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>üö™ Wyloguj siƒô</button>
        </div>
      </aside>

      {/* TRE≈öƒÜ */}
      <main className="main-content">
        
        {/* KARTA SALDA (Globalne) */}
        <div className="balance-card">
          <h3>Aktualne saldo konta</h3>
          <div className={`balance-amount ${balance < 0 ? 'negative' : ''}`}>
            {balance.toFixed(2)} PLN
          </div>
        </div>

        {/* NOWO≈öƒÜ: PASEK FILTROWANIA DATY */}
        <div className="date-filter-section">
          <label>Zakres od:</label>
          <input 
            type="date" 
            className="date-input"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
          
          <span className="date-separator">‚Äî</span>
          
          <label>do:</label>
          <input 
            type="date" 
            className="date-input"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />

          <button 
             onClick={() => { setStartDate(''); setEndDate(''); }}
             style={{marginLeft: 10, padding: '5px 10px', cursor:'pointer', border:'none', background:'#eee', borderRadius: 4}}
          >
             Poka≈º wszystko
          </button>
        </div>

        <div className="widgets-area">
          
          {/* WYKRES (Filtrowany) */}
          <div className="widget-card">
            <h3>Wydatki w wybranym okresie</h3>
            <div style={{ width: '100%', height: 300 }}>
              {chartData.length > 0 ? (
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={chartData}
                      cx="50%"
                      cy="50%"
                      labelLine={true}
                      label={({ name, percent }: any) => `${name} ${(percent * 100).toFixed(0)}%`} 
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                    >
                      {chartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value: any) => `${Number(value).toFixed(2)} PLN`} />
                    <Legend verticalAlign="bottom" height={36}/>
                  </PieChart>
                </ResponsiveContainer>
              ) : (
                <div style={{textAlign: 'center', marginTop: 100, color: '#95a5a6'}}>
                  Brak wydatk√≥w w tym terminie.
                </div>
              )}
            </div>
          </div>

          {/* LISTA (Filtrowana) */}
          <div className="widget-card">
            <h3>Lista z wybranego okresu</h3>
            <ul className="transaction-list">
              {displayedExpenses.length > 0 ? (
                // Wy≈õwietlamy max 10 na li≈õcie, ≈ºeby nie by≈Ça za d≈Çuga
                displayedExpenses.slice(0, 10).map(exp => {
                  const matchedCat = categories.find(c => c.id === exp.categoryId);
                  const catName = matchedCat ? matchedCat.name : "Inne";

                  return (
                    <li key={exp.id} className="transaction-item">
                      <div className="transaction-info">
                        <strong>{catName}</strong>
                        <span>
                          {new Date(exp.date).toLocaleDateString()} 
                          {exp.note ? ` ‚Ä¢ ${exp.note}` : ''}
                        </span>
                      </div>
                      <div className="amount-red">
                        -{exp.amount.toFixed(2)} PLN
                      </div>
                    </li>
                  );
                })
              ) : (
                <p style={{textAlign: 'center', color: '#95a5a6', marginTop: 50}}>
                  Brak transakcji w tym okresie.
                </p>
              )}
            </ul>
            
            {displayedExpenses.length > 10 && (
              <div style={{marginTop: 10, textAlign: 'center', fontSize: '0.8rem', color: '#888'}}>
                (Pokazano 10 najnowszych z {displayedExpenses.length})
              </div>
            )}
          </div>

        </div>
      </main>
    </div>
  );
};

export default Dashboard;
