// src/pages/IncomePage.tsx
import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';
import apiClient from '../api/axios';
import { Income, CreateIncomeRequest } from '../types'; // Upewnij siƒô, ≈ºe masz te typy
import '../styles/dashboard.css';
import '../styles/income.css'; // Nowy plik styl√≥w

const IncomePage = () => {
  // Stany
  const [incomes, setIncomes] = useState<Income[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  
  // Stan edycji (ID jako string, bo to GUID)
  const [editingId, setEditingId] = useState<string | null>(null);

  const { register, handleSubmit, reset, setValue, formState: { errors } } = useForm<CreateIncomeRequest>({
    defaultValues: {
      date: new Date().toISOString().split('T')[0] // Domy≈õlnie dzisiaj
    }
  });

  // 1. Pobieranie listy przychod√≥w
  const fetchIncomes = async () => {
    try {
      const response = await apiClient.get<Income[]>('/api/Income');
      // Sortowanie: najnowsze na g√≥rze
      const sorted = response.data.sort((a, b) => 
        new Date(b.date).getTime() - new Date(a.date).getTime()
      );
      setIncomes(sorted);
    } catch (error) {
      console.error("B≈ÇƒÖd pobierania przychod√≥w:", error);
    }
  };

  useEffect(() => {
    fetchIncomes();
  }, []);

  // 2. Obs≈Çuga zapisu (Dodawanie / Edycja)
  const onSubmit = async (data: CreateIncomeRequest) => {
    setIsLoading(true);
    try {
      const payload = {
        amount: Number(data.amount),
        date: new Date(data.date).toISOString(),
        source: data.source // "Nazwa" przychodu
      };

      if (editingId) {
        // EDYCJA (PUT)
        await apiClient.put(`/api/Income/${editingId}`, payload);
      } else {
        // DODAWANIE (POST)
        await apiClient.post('/api/Income', payload);
      }
      
      resetForm();
      fetchIncomes(); // Od≈õwie≈º listƒô
    } catch (error) {
      console.error("B≈ÇƒÖd zapisu:", error);
      alert("WystƒÖpi≈Ç b≈ÇƒÖd podczas zapisu.");
    } finally {
      setIsLoading(false);
    }
  };

  // 3. Usuwanie
  const handleDelete = async (id: string) => {
    if (!window.confirm("Czy na pewno chcesz usunƒÖƒá ten przych√≥d?")) return;
    try {
      await apiClient.delete(`/api/Income/${id}`);
      setIncomes(prev => prev.filter(i => i.id !== id));
    } catch (error) {
      console.error("B≈ÇƒÖd usuwania:", error);
      alert("Nie uda≈Ço siƒô usunƒÖƒá wpisu.");
    }
  };

  // 4. Rozpoczƒôcie edycji
  const handleEdit = (income: Income) => {
    setEditingId(income.id);
    setValue("source", income.source || "");
    setValue("amount", income.amount);
    setValue("date", new Date(income.date).toISOString().split('T')[0]+1);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // 5. Reset formularza
  const resetForm = () => {
    setEditingId(null);
    reset({
      source: "",
      amount: undefined,
      date: new Date().toISOString().split('T')[0]
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  return (
    <div className="dashboard-container">
      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2>üí∞ MyBudget</h2>
        <nav>
          <Link to="/">üìä Pulpit</Link>
          <Link to="/expenses">üí∏ Wydatki</Link>
          <Link to="/income" style={{ color: 'white', fontWeight: 'bold' }}>üí∞ Przychody</Link>
          <Link to="/settings">‚öôÔ∏è Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>üö™ Wyloguj siƒô</button>
        </div>
      </aside>

      {/* TRE≈öƒÜ */}
      <main className="main-content">
        <h1>Twoje Przychody</h1>

        {/* --- FORMULARZ --- */}
        <div className="income-form-card" style={editingId ? {border: '2px solid #ffc107'} : {}}>
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 15}}>
            <h3>{editingId ? 'Edytuj przych√≥d' : 'Dodaj nowy przych√≥d'}</h3>
            {editingId && <button onClick={resetForm} className="cancel-btn">Anuluj</button>}
          </div>

          <form onSubmit={handleSubmit(onSubmit)} className="expense-form">
            
            <div className="form-group-inline" style={{flexGrow: 2}}>
              <label>Nazwa (≈πr√≥d≈Ço)</label>
              <input 
                type="text" 
                placeholder="np. Wyp≈Çata, Sprzeda≈º..."
                {...register("source", { required: true })} 
              />
            </div>

            <div className="form-group-inline">
              <label>Kwota (PLN)</label>
              <input 
                type="number" 
                step="0.01" 
                {...register("amount", { required: true, min: 0.01 })} 
              />
            </div>

            <div className="form-group-inline">
              <label>Data</label>
              <input 
                type="date" 
                {...register("date", { required: true })} 
              />
            </div>

            <button type="submit" disabled={isLoading} className={editingId ? "save-btn" : "add-btn"}>
              {editingId ? 'Zapisz' : 'Dodaj'}
            </button>
          </form>
        </div>

        {/* --- TABELA --- */}
        <div className="table-card">
          <table className="custom-table">
            <thead>
              <tr>
                <th>Data</th>
                <th>Nazwa</th>
                <th style={{textAlign: 'right'}}>Kwota</th>
                <th style={{textAlign: 'center'}}>Akcje</th>
              </tr>
            </thead>
            <tbody>
              {incomes.map((inc) => (
                <tr key={inc.id} style={editingId === inc.id ? {backgroundColor: '#fff3cd'} : {}}>
                  <td>{new Date(inc.date).toLocaleDateString()}</td>
                  <td>{inc.source}</td>
                  <td style={{textAlign: 'right', fontWeight: 'bold', color: '#28a745'}}>
                    +{inc.amount.toFixed(2)} PLN
                  </td>
                  <td style={{textAlign: 'center'}}>
                    <button onClick={() => handleEdit(inc)} className="action-btn edit-btn">‚úèÔ∏è</button>
                    <button onClick={() => handleDelete(inc.id)} className="action-btn delete-btn">üóëÔ∏è</button>
                  </td>
                </tr>
              ))}
              {incomes.length === 0 && (
                <tr><td colSpan={4} style={{textAlign: 'center', padding: 20}}>Brak przychod√≥w</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

export default IncomePage;
