// src/pages/SettingsPage.tsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import apiClient from '../api/axios';
import { Category } from '../types'; // Upewnij siƒô, ≈ºe w types masz Category { id: string, name: string }
import '../styles/dashboard.css';
import '../styles/settings.css';

const SettingsPage = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // Stan do edycji
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingName, setEditingName] = useState("");

  // 1. POBIERANIE KATEGORII
  const fetchCategories = async () => {
    try {
      const res = await apiClient.get<Category[]>('/api/Category');
      // Sortujemy alfabetycznie dla porzƒÖdku
      const sorted = res.data.sort((a, b) => a.name.localeCompare(b.name));
      setCategories(sorted);
    } catch (error) {
      console.error("B≈ÇƒÖd pobierania kategorii:", error);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  // 2. DODAWANIE NOWEJ
  const handleAdd = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newCategoryName.trim()) return;

    try {
      await apiClient.post('/api/Category', { name: newCategoryName });
      setNewCategoryName(""); // Wyczy≈õƒá input
      fetchCategories(); // Od≈õwie≈º listƒô
    } catch (error) {
      alert("Nie uda≈Ço siƒô dodaƒá kategorii.");
      console.error(error);
    }
  };

  // 3. USUWANIE
  const handleDelete = async (id: string) => {
    if (!window.confirm("Czy na pewno usunƒÖƒá tƒô kategoriƒô?")) return;

    try {
      await apiClient.delete(`/api/Category/${id}`);
      // Usuwamy lokalnie z listy (szybciej ni≈º fetch)
      setCategories(prev => prev.filter(c => c.id !== id));
    } catch (error) {
      alert("Nie uda≈Ço siƒô usunƒÖƒá (byƒá mo≈ºe jest u≈ºywana w wydatkach?).");
      console.error(error);
    }
  };

  // 4. ROZPOCZƒòCIE EDYCJI
  const startEdit = (cat: Category) => {
    setEditingId(cat.id);
    setEditingName(cat.name);
  };

  // 5. ZAPISANIE EDYCJI
  const saveEdit = async () => {
    try {
      // Zak≈Çadam, ≈ºe endpoint to PUT /api/Category z obiektem {id, name}
      // Je≈õli backend wymaga ID w URL, zmie≈Ñ na: .put(`/api/Category/${editingId}`, ...)
      await apiClient.put('/api/Category', { 
        id: editingId, 
        name: editingName 
      });
      
      setEditingId(null);
      fetchCategories();
    } catch (error) {
      alert("B≈ÇƒÖd edycji kategorii.");
      console.error(error);
    }
  };

  // 6. FUNKCJA POMOCNICZA: ≈ÅADOWANIE DOMY≈öLNYCH (z poprzedniej lekcji)
  const handleLoadDefaults = async () => {
    if(!window.confirm("Dodaƒá domy≈õlne kategorie?")) return;
    setIsLoading(true);
    const defaults = ["Jedzenie", "Mieszkanie", "Transport", "Rozrywka", "Inne"];
    try {
      for (const name of defaults) {
        // Sprawdzamy prymitywnie czy ju≈º jest na li≈õcie pobranej w React
        if (!categories.some(c => c.name.toLowerCase() === name.toLowerCase())) {
          await apiClient.post('/api/Category', { name });
        }
      }
      await fetchCategories();
      alert("Gotowe!");
    } catch (e) { console.error(e); } 
    finally { setIsLoading(false); }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2>MyBudget</h2>
        <nav>
          <Link to="/">Pulpit</Link>
          <Link to="/expenses">Wydatki</Link>
          <Link to="/income">Przychody</Link>
          <Link to="/settings" style={{ color: 'white', fontWeight: 'bold' }}>Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>Wyloguj siƒô</button>
        </div>
      </aside>

      <main className="main-content">
        <h1>Ustawienia</h1>

        <div className="settings-card">
          <h3>ZarzƒÖdzaj Kategoriami</h3>

          {/* FORMULARZ DODAWANIA */}
          <form onSubmit={handleAdd} className="add-category-form">
            <input 
              type="text" 
              className="add-category-input"
              placeholder="Nowa kategoria (np. Hobby)..."
              value={newCategoryName}
              onChange={e => setNewCategoryName(e.target.value)}
            />
            <button type="submit" className="add-btn">Dodaj</button>
          </form>

          {/* LISTA KATEGORII */}
          <ul className="categories-list">
            {categories.map(cat => (
              <li key={cat.id} className="category-item">
                {editingId === cat.id ? (
                  /* TRYB EDYCJI WIESZA */
                  <>
                    <input 
                      type="text" 
                      className="edit-input"
                      value={editingName} 
                      onChange={e => setEditingName(e.target.value)}
                    />
                    <div className="cat-actions">
                      <button onClick={saveEdit} className="icon-btn btn-save" title="Zapisz">üíæ</button>
                      <button onClick={() => setEditingId(null)} className="icon-btn btn-cancel" title="Anuluj">‚ùå</button>
                    </div>
                  </>
                ) : (
                  /* TRYB PODGLƒÑDU WIESZA */
                  <>
                    <span>{cat.name}</span>
                    <div className="cat-actions">
                      <button onClick={() => startEdit(cat)} className="icon-btn btn-edit" title="Edytuj">‚úèÔ∏è</button>
                      <button onClick={() => handleDelete(cat.id)} className="icon-btn btn-delete" title="Usu≈Ñ">üóëÔ∏è</button>
                    </div>
                  </>
                )}
              </li>
            ))}
            {categories.length === 0 && <p style={{color:'#999', textAlign:'center'}}>Brak kategorii.</p>}
          </ul>

          {/* Opcja awaryjna na dole */}
          <div className="defaults-section">
             <button onClick={handleLoadDefaults} disabled={isLoading} className="load-defaults-link">
               {isLoading ? "≈Åadowanie..." : "Za≈Çaduj domy≈õlne kategorie (je≈õli brakuje)"}
             </button>
          </div>

        </div>
      </main>
    </div>
  );
};

export default SettingsPage;
