// src/pages/Dashboard.tsx
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import apiClient from '../api/axios';
import { Expense, Income, Category } from '../types'; // Dodajemy import Category
import '../styles/dashboard.css';

const COLORS = ['#3498db', '#2ecc71', '#9b59b6', '#f1c40f', '#e67e22', '#e74c3c', '#95a5a6'];

const Dashboard = () => {
  const navigate = useNavigate();
  
  // Stany
  const [balance, setBalance] = useState<number>(0);
  const [recentExpenses, setRecentExpenses] = useState<Expense[]>([]);
  // Nowy stan do przechowywania kategorii (potrzebny do listy ostatnich)
  const [categories, setCategories] = useState<Category[]>([]); 
  const [chartData, setChartData] = useState<{ name: string; value: number }[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);

        // 1. Pobieramy WSZYSTKO naraz (Wydatki, Dochody, Kategorie)
        const [expensesRes, incomeRes, categoriesRes] = await Promise.all([
          apiClient.get<Expense[]>('/api/Expenses'),
          apiClient.get<Income[]>('/api/Income'),
          apiClient.get<Category[]>('/api/Category')
        ]);

        const expenses = expensesRes.data;
        const income = incomeRes.data;
        const fetchedCategories = categoriesRes.data;
        
        setCategories(fetchedCategories); // Zapisujemy kategorie do stanu

        // 2. Obliczamy SALDO
        const totalIncome = income.reduce((sum, item) => sum + item.amount, 0);
        const totalExpenses = expenses.reduce((sum, item) => sum + item.amount, 0);
        setBalance(totalIncome - totalExpenses);

        // 3. Sortujemy wydatki (5 najnowszych)
        const sortedExpenses = [...expenses].sort((a, b) => 
          new Date(b.date).getTime() - new Date(a.date).getTime()
        );
        setRecentExpenses(sortedExpenses.slice(0, 5));

        // 4. Przygotowujemy dane do WYKRESU
        // T≈ÅUMACZENIE ID -> NAZWA
        const categoryMap = new Map<string, number>();

        expenses.forEach(exp => {
          // Szukamy kategorii po ID
          const matchedCat = fetchedCategories.find(c => c.id === exp.categoryId);
          // Je≈õli znaleziono, bierzemy nazwƒô. Jak nie - "Inne".
          const catName = matchedCat ? matchedCat.name : "Inne";
          
          const currentTotal = categoryMap.get(catName) || 0;
          categoryMap.set(catName, currentTotal + exp.amount);
        });

        const chartDataArray = Array.from(categoryMap, ([name, value]) => ({ name, value }));
        setChartData(chartDataArray);

      } catch (error) {
        console.error("B≈ÇƒÖd pobierania danych:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  if (isLoading) {
    return (
      <div className="dashboard-container" style={{display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
        <h2>≈Åadowanie danych...</h2>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2>üí∞ MyBudget</h2>
        <nav>
          <Link to="/">üìä Pulpit</Link>
          <Link to="/expenses">üí∏ Wydatki</Link>
          <Link to="/income">üí∞ Przychody</Link>
          <Link to="/settings">‚öôÔ∏è Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>üö™ Wyloguj siƒô</button>
        </div>
      </aside>

      {/* G≈Å√ìWNA TRE≈öƒÜ */}
      <main className="main-content">
        
        {/* KARTA SALDA */}
        <div className="balance-card">
          <h3>Aktualne saldo</h3>
          <div className={`balance-amount ${balance < 0 ? 'negative' : ''}`}>
            {balance.toFixed(2)} PLN
          </div>
        </div>

        {/* WIDGETY */}
        <div className="widgets-area">
          
          {/* 1. WYKRES KO≈ÅOWY */}
          {/* 1. WYKRES KO≈ÅOWY */}
          <div className="widget-card">
            <h3>Gdzie uciekajƒÖ pieniƒÖdze?</h3>
            
            {/* WA≈ªNE: Ten div musi mieƒá ustalonƒÖ wysoko≈õƒá! Bez tego wykres zniknie. */}
            <div style={{ width: '100%', height: 300 }}>
              {chartData.length > 0 ? (
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={chartData}
                      cx="50%"
                      cy="50%"
                      labelLine={true} // W≈ÇƒÖczamy liniƒô, ≈ºeby napisy nie nachodzi≈Çy na wykres
                      // Prostsza funkcja wy≈õwietlania etykiet
                      label={({ name, percent }: any) => `${name} ${(percent * 100).toFixed(0)}%`} 
                      outerRadius={80} // Zmniejszamy promie≈Ñ (z 100 na 80), ≈ºeby by≈Ço miejsce na napisy
                      fill="#8884d8"
                      dataKey="value"
                    >
                      {chartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value: any) => `${Number(value).toFixed(2)} PLN`} />
                    <Legend verticalAlign="bottom" height={36}/>
                  </PieChart>
                </ResponsiveContainer>
              ) : (
                <div style={{textAlign: 'center', marginTop: 100, color: '#95a5a6'}}>
                  Brak wydatk√≥w.
                </div>
              )}
            </div>
          </div>
          
          {/* 2. LISTA OSTATNICH WYDATK√ìW */}
          <div className="widget-card">
            <h3>Ostatnie wydatki</h3>
            <ul className="transaction-list">
              {recentExpenses.length > 0 ? (
                recentExpenses.map(exp => {
                  // T≈ÅUMACZENIE ID -> NAZWA (Dla ka≈ºdego wiersza)
                  const matchedCat = categories.find(c => c.id === exp.categoryId);
                  const catName = matchedCat ? matchedCat.name : "Inne";

                  return (
                    <li key={exp.id} className="transaction-item">
                      <div className="transaction-info">
                        <strong>{catName}</strong>
                        <span>
                          {new Date(exp.date).toLocaleDateString()} 
                          {exp.note ? ` ‚Ä¢ ${exp.note}` : ''}
                        </span>
                      </div>
                      <div className="amount-red">
                        -{exp.amount.toFixed(2)} PLN
                      </div>
                    </li>
                  );
                })
              ) : (
                <p style={{textAlign: 'center', color: '#95a5a6', marginTop: 50}}>
                  Brak historii transakcji.
                </p>
              )}
            </ul>
            
            {recentExpenses.length > 0 && (
              <div style={{marginTop: 20, textAlign: 'right'}}>
                <Link to="/expenses" style={{color: '#3498db', fontWeight: 'bold'}}>
                  Zobacz wszystkie &rarr;
                </Link>
              </div>
            )}
          </div>

        </div>
      </main>
    </div>
  );
};

export default Dashboard;
