// src/App.tsx
import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';

// Importujemy nasze strony
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import Dashboard from './pages/Dashboard';       
import ExpensesPage from './pages/ExpensesPage'; 
import SettingsPage from './pages/SettingsPage';
import IncomePage from './pages/IncomePage';


// Komponent chroniący trasy (sprawdza czy jest user)
const PrivateRoute = ({ children }: { children: React.ReactNode }) => {
  const user = localStorage.getItem('user');

  // Jeśli user istnieje w localStorage, wyświetl treść (np. Dashboard)
  // Jeśli nie, przekieruj do /login
  return user ? <>{children}</> : <Navigate to="/login" />;
};

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Trasy publiczne */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* Trasy chronione (Dostępne tylko po zalogowaniu) */}
        <Route 
          path="/" 
          element={
            <PrivateRoute>
              <Dashboard />  {/* <--- Tutaj wyświetlamy Dashboard na stronie głównej */}
            </PrivateRoute>
          } 
        />

        <Route 
          path="/expenses" 
          element={
            <PrivateRoute>
              <ExpensesPage />
            </PrivateRoute>
          } 
        />

        <Route 
          path="/income" 
          element={
            <PrivateRoute>
              <IncomePage />
            </PrivateRoute>
          } 
        />

        <Route 
          path="/settings" 
          element={<PrivateRoute><SettingsPage /></PrivateRoute>} 
        />

        <Route path="*" element={<Navigate to="/" />} />

      </Routes>
    </BrowserRouter>
  );
}

export default App;
