// src/pages/SettingsPage.tsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import apiClient from '../api/axios';
import '../styles/dashboard.css'; // Og√≥lny uk≈Çad strony
import '../styles/settings.css';  // <--- NOWY IMPORT STYLI DLA TEJ STRONY

const SettingsPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  const handleInitCategories = async () => {
    // (Lista kategorii - mo≈ºesz dodaƒá wiƒôcej wed≈Çug uznania)
    const defaultCategories = [
        "Jedzenie", "Mieszkanie", "Transport", 
        "Rozrywka", "Inne", "Zakupy", "Zdrowie", 
        "Edukacja", "Oszczƒôdno≈õci"
    ];
    
    if(!window.confirm("Ta operacja doda podstawowe kategorie do Twojego konta (je≈õli jeszcze ich nie masz). Kontynuowaƒá?")) return;

    setIsLoading(true);
    let successCount = 0;

    try {
      // 1. Pobieramy obecne kategorie
      const currentCatsRes = await apiClient.get('/api/Category');
      // Mapujemy na same nazwy i zamieniamy na ma≈Çe litery dla ≈Çatwiejszego por√≥wnania
      const currentNames = currentCatsRes.data.map((c: any) => c.name.toLowerCase());

      // 2. Dodajemy tylko te, kt√≥rych brakuje
      for (const name of defaultCategories) {
        // Sprawdzamy czy kategoria (ma≈Çymi literami) ju≈º istnieje
        if (!currentNames.includes(name.toLowerCase())) {
          await apiClient.post('/api/Category', { name });
          successCount++;
        }
      }
      
      if (successCount > 0) {
          alert(`Sukces! Dodano ${successCount} nowych kategorii.`);
      } else {
          alert("Twoje konto ma ju≈º wszystkie domy≈õlne kategorie.");
      }
      
    } catch (error) {
      console.error("B≈ÇƒÖd podczas inicjalizacji kategorii:", error);
      alert("WystƒÖpi≈Ç b≈ÇƒÖd podczas ≈ÇƒÖczenia z serwerem. Sprawd≈∫ konsolƒô (F12).");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      {/* --- SIDEBAR --- */}
      <aside className="sidebar">
        <h2>MyBudget</h2>
        <nav>
          <Link to="/">Pulpit</Link>
          <Link to="/expenses">Wydatki</Link>
          <Link to="/income">Przychody</Link>
          <Link to="/settings" style={{ color: 'white', fontWeight: 'bold' }}>Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>Wyloguj siƒô</button>
        </div>
      </aside>

      {/* --- TRE≈öƒÜ --- */}
      <main className="main-content">
        <h1>Ustawienia</h1>

        {/* U≈ºywamy klasy zamiast style={{...}} */}
        <div className="settings-card">
          <h3>‚öôÔ∏è Konfiguracja konta</h3>
          <p className="settings-description">
            Kliknij poni≈ºszy przycisk, aby sprawdziƒá i dodaƒá brakujƒÖce kategorie startowe (np. Jedzenie, Transport) do swojego konta. Operacja jest bezpieczna ‚Äì nie zduplikuje istniejƒÖcych kategorii.
          </p>
          
          <button 
            onClick={handleInitCategories} 
            disabled={isLoading}
            className="load-categories-btn" // U≈ºywamy klasy
          >
            {/* Ma≈Ça ikona przed tekstem */}
            <span>üì•</span> 
            {isLoading ? 'Sprawdzanie...' : 'Za≈Çaduj domy≈õlne kategorie'}
          </button>
        </div>
      </main>
    </div>
  );
};

export default SettingsPage;
