// src/pages/SettingsPage.tsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import apiClient from '../api/axios';
import '../styles/dashboard.css';

const SettingsPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Funkcja wylogowania
  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.reload();
  };

  // Funkcja dodajƒÖca domy≈õlne kategorie
  const handleInitCategories = async () => {
    const defaultCategories = ["Jedzenie", "Mieszkanie", "Transport", "Rozrywka", "Inne", "Zakupy", "Zdrowie"];
    
    if(!window.confirm("Ta operacja doda podstawowe kategorie do Twojego konta. Kontynuowaƒá?")) return;

    setIsLoading(true);
    let successCount = 0;

    try {
      // Pobieramy obecne kategorie, ≈ºeby nie dodawaƒá dubli
      // (zak≈Çadamy, ≈ºe backend zwraca listƒô, jak widzieli≈õmy na screenie)
      const currentCatsRes = await apiClient.get('/api/Category');
      const currentNames = currentCatsRes.data.map((c: any) => c.name);

      for (const name of defaultCategories) {
        if (!currentNames.includes(name)) {
          await apiClient.post('/api/Category', { name });
          successCount++;
        }
      }
      
      alert(`Zako≈Ñczono! Dodano ${successCount} nowych kategorii.`);
    } catch (error) {
      console.error("B≈ÇƒÖd podczas inicjalizacji kategorii:", error);
      alert("WystƒÖpi≈Ç b≈ÇƒÖd podczas ≈ÇƒÖczenia z serwerem.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      {/* --- SIDEBAR --- */}
      <aside className="sidebar">
        <h2>MyBudget</h2>
        <nav>
          <Link to="/">Pulpit</Link>
          <Link to="/expenses">Wydatki</Link>
          <Link to="/income">Przychody</Link>
          {/* Pod≈õwietlamy aktywny link */}
          <Link to="/settings" style={{ color: 'white', fontWeight: 'bold' }}>Ustawienia</Link>
        </nav>
        <div style={{ marginTop: 'auto' }}>
          <button onClick={handleLogout}>Wyloguj siƒô</button>
        </div>
      </aside>

      {/* --- TRE≈öƒÜ --- */}
      <main className="main-content">
        <h1>Ustawienia</h1>

        <div style={{ 
            background: 'white', 
            padding: '20px', 
            borderRadius: '10px', 
            boxShadow: '0 4px 6px rgba(0,0,0,0.05)',
            maxWidth: '600px' 
        }}>
          <h3>‚öôÔ∏è Konfiguracja konta</h3>
          <p style={{ color: '#666', marginBottom: '20px' }}>
            Je≈õli to Twoje nowe konto, mo≈ºesz automatycznie dodaƒá zestaw startowych kategorii (np. Jedzenie, Transport), aby nie wpisywaƒá ich rƒôcznie.
          </p>
          
          <button 
            onClick={handleInitCategories} 
            disabled={isLoading}
            style={{
              padding: '12px 20px',
              backgroundColor: '#6c757d',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              fontSize: '1rem'
            }}
          >
            {isLoading ? 'Przetwarzanie...' : 'üì• Za≈Çaduj domy≈õlne kategorie'}
          </button>
        </div>

        {/* Tu w przysz≈Ço≈õci dodasz zmianƒô has≈Ça, zmianƒô waluty itp. */}
      </main>
    </div>
  );
};

export default SettingsPage;
